div.script:
function updateFontColor(hexColor) {
  const baseUrl = "https://capsule-render.vercel.app/api";
  const params = new URLSearchParams({
    type: "waving",
    color: "0:7ea3fb,100:ef6ec1",
    height: "100",
    section: "header",
    text: "Angel Quintero",
    fontSize: "60",
    fontColor: hexColor.replace("#", ""),
    font: "Sans-serif"
  });
  document.getElementById("capsuleBanner").src = `${baseUrl}?${params.toString()}`;
}

// Example: change font color to blue after 2 seconds
setTimeout(() => updateFontColor("#0000ff"), 2000);
